<<<<<<<<<<<<<< playground code >>>>>>>>>>>>>>>>


// create the tree
var list = new qx.ui.list.List();
list.set({
  width: 150,
  height: 300
});
this.getRoot().add(list, {left: 10, top: 10});

var persons = new qx.data.Array();
for(var i=0;i<20;i++){
  persons.push(qx.data.marshal.Json.createModel({name: ("Name "+i), age : i}));
};

var delegate = {
    sorter : function(a, b) {
      var aOlder = this.group(a);
      var bOlder = this.group(b);
      if (aOlder < bOlder) { return 1; }
      else if (aOlder > bOlder) { return -1;}
      else {
				var aLabel = a.getAge();
				var bLabel = b.getAge();
        return aLabel > bLabel ? 1 : aLabel == bLabel ? 0 : -1;
			}
    },
    bindItem : function(controller, item, id) {
      controller.bindProperty("name", "label",null , item, id);
      //controller.bindProperty("", "model",null , item, id);
    },
    configureGroupItem : function(group) {
      group.setPadding(3);
      group.setBackgroundColor("#AAA");
    },
    bindGroupItem : function(controller, item, id) {
        controller.bindProperty("", "value", {
          converter : function(data){
        		if(data){ return "older then 20"; }
            else{ return "younger then 20"; }
        	}
        }, item, id);
    },
    group : function(person) {
      return person.getAge()<=20;
    }
};
list.setDelegate(delegate);
list.setModel(persons);


var button = new qx.ui.form.Button("Bam!");
button.addListener("execute", function(){
  persons.getItem(8).setAge(21);
  list.refresh();
},this);
this.getRoot().add(button, {left: 200, top: 10});
